//去查看商品详情
function gotoViewGood(wmsMiId) {
    var newTab = window.open('about:blank');
    newTab.location.href = '/dentistmall/xiangxi.htm?wmsMiId=' + wmsMiId;
}

//商品名称
function renderMatName(val, item, rowIndex) {
    let str = '';
    str += '<a onclick="gotoViewGood(' + item.wmsMiId + ')">' + item.matName + '</a>';
    return str;
}

//图标
function renderLogo(val, item, rowIdex) {
    let str = '';
    if (item.lessenFilePath == undefined)
        str = '';
    else
        str += '<img onclick="gotoViewGood(' + item.wmsMiId + ')" src="' + item.lessenFilePath + '" style=\"width: 40px; height: 40px;cursor: pointer\" />';
    return str;
}

// SKU
function renderSKU(val, item, rowIndex) {
    let str = '';
    // str += '<img onclick="viewMaterielNorm(' + item.wmsMiId + ')" style="width: 25px;" src="/dentistmall/img/sku.png" />';
    str += '<img onclick="viewMaterielNorm(\'' + item.matCode + '\',' + item.wmsMiId + ')" style="width: 25px;" src="/dentistmall/img/sku.png" />';
    return str;
}

//打开SKU窗口
function viewMaterielNorm(matCode, wmsMiId) {
    var data = "wmsMiId=" + wmsMiId + '&matCode=' + matCode;
    var att_url = $.supper("getServicePath", {"data": data, url: "/jsp/dentistmall/business/viewGoodSkuInfo.jsp"});
    var tt_win = $.supper("showWin", {
        url: att_url,
        title: "SKU价格",
        icon: "fa-th-list",
        width: 800, height: 600
    });
}

//销量
function renderNum(val, item, rowIdex) {
    let str = '';
    if (item.number1 == undefined || item.number1 == null || item.number1 == '')
        str = '0';
    else
        str = item.number1;
    return str;
}

//上架状态
function formatState(val, item, rowIndex) {
    let str = '';
    if (item.state == 1) {
        str += '<span>已上架</span>';
    } else if (item.state == 2) {
        str += '<span>下架</span>';
    } else if (item.state == 3) {
        str += '<span>违规下架</span>';
    } else {
        str += '';
    }
    return str;
}

// 日志
function renderLog(val, item, rowIndex) {
    let str = '';
    str += '<img onclick="toDetailLog(' + item.wmsMiId + ')" style="width: 25px;" src="/dentistmall/img/format_log.png" />';
    return str;
}

// 查看日志
function toDetailLog(wmsMiId) {
    var data = "wmsMiId=" + wmsMiId;
    var att_url = $.supper("getServicePath", {
        'data': data,
        url: "/jsp/dentistmall/business/vieGoodOperationLog.jsp"
    });
    var tt_win = $.supper("showWin", {
        url: att_url, title: "商品日志", icon: "fa-th-list", width: 800, height: 520
    });
}

//操作
function control(val, item, rowIndex) {
    let str = '';
    str += '<a onclick="gotoViewGood(' + item.wmsMiId + ')" >详情</a>&nbsp;&nbsp;';
    if (item.state == 1) {
        str += '<img onclick="open_Off_Shelf_Goods_Box(' + item.wmsMiId + ')" style="width: 25px;" src="/dentistmall/img/xiajia.png" />';
    }
    return str;
}

