var _brandnamearr = [];//选中品牌名字数组
var _brandnamestr;//选中品牌名字拼接
function selectBrand(ii, jj) {
    if (_brandids.length == 0) {
        _brandids[0] = {
            ii: ii,
            jj: jj
        };
        var branddd = _current_brand_list[ii].list[jj].brand;
        _brandnamearr.push(branddd);
        _brandnamestr = branddd + ',';
        $("#bselectItem_" + ii + '_' + jj).addClass(' lion');
        $("#bcheckItem_" + ii + '_' + jj).prop("checked", true);
    } else {
        var bstate = false;
        var aaa = []
        aaa[0] = {
            ii: ii,
            jj: jj
        };
        for (var i = 0; i < _brandids.length; i++) {
            if (_brandids[i].ii == ii && _brandids[i].jj == jj) {
                _brandids.splice(i, 1);
                _brandnamearr.splice(i, 1);
                bstate = false;
            } else {
                bstate = true;
            }
        }
        if (bstate == true) {
            $("#bselectItem_" + ii + '_' + jj).addClass(' lion');
            $("#bcheckItem_" + ii + '_' + jj).prop("checked", true);
            _brandids = _brandids.concat(aaa);
            _brandnamearr = _brandnamearr.concat(_current_brand_list[ii].list[jj].brand);
        } else {
            $("#bselectItem_" + ii + '_' + jj).removeClass(' lion');
            $("#bcheckItem_" + ii + '_' + jj).prop("checked", false);
        }
    }
    _brandnamestr = _brandnamearr.join(',');
    if (_brandnamestr == '') {
        $("#cancelBrandNamesBtn").html('');
        $("#selectBrandNames").html('暂无');
    } else {
        $("#cancelBrandNamesBtn").html('取消选择');
        $("#selectBrandNames").html(_brandnamestr);
    }
}

